import React, { useEffect, useState } from 'react';
import {Routes, Route, Navigate} from 'react-router-dom';
import AdminAccount from './components/admin/AdminAccount';
import Dashboard from './components/admin/Dashboard';
import Login from './components/frontend/auth/Login';

import axios from 'axios';

axios.defaults.withCredentials = true;
axios.interceptors.request.use(function (config) {
  const token = localStorage.getItem('auth_token');
  config.headers.Authorization = token ? `Bearer ${token}` : '';
  return config;
});

axios.defaults.headers.post['Accept'] = 'application/json';
axios.defaults.headers.post['Content-Type'] = 'application/json';
axios.defaults.baseURL = "http://localhost:8000/";


function AdminRoutes() {
  const [Authenticated, setAuthenticated] = useState(initialState);
  const [loading, setLoading] = useState(initialState);

  useEffect(() => {
    axios.get('/api/checkUser').then(res => {
      if (res.status === 200) {
        setAuthenticated(true);
      }
    });

    return () => {
      setAuthenticated(false);
    };
  }, []);

  if (loading) {
    return <h1>Loading...</h1>
  }

  return (
    <Routes>
      <Route path='dashboard' element={ localStorage.getItem('auth_token') ? <Dashboard /> : <Login/> } />
        <Route path='account' element={ localStorage.getItem('auth_token') ? <AdminAccount /> : <Login/> } />
     </Routes>
  );
}

export default AdminRoutes;
