import React, { useEffect, useState } from 'react';
import { Link, useParams, useNavigate } from 'react-router-dom';

import axios from 'axios';
import swal from 'sweetalert';

function Cart() {
    const [cart, setCart] = useState([]);

    const navigate = useNavigate();
    const [loading, setLoading] = useState(true);

    if (!localStorage.getItem('auth_token')) {
        navigate('/categories');
        swal("Molimo Vas da se prijavite kako biste pristupili svojoj košarici", "poruka", "error");
    }

    useEffect(() => {

        document.title = "Moja tržnica";

        axios.get(`/api/my-shopping-cart/`).then(res => {
            if (res.data.status === 200) {
                setCart(res.data.list);
                setLoading(false);
            } else if (res.data.status === 401) {
                swal("Molimo Vas da se prijavite kako biste pristupili svojoj košarici", res.data.message, "error");
                navigate('/categories');
            }
        });
        setLoading(false);
    }, []);

    if (loading) {
        return <h4>Dohvaćanje podataka u tijeku...</h4>
    }

    return (
        <div>
            <div className='py-3 bg-warning'>
                <div className='container'>
                    <h6>Moja tržnica / Diana</h6>
                </div>
            </div>

            <div className='py-4'>
                <div className='container'>
                    <div className='row'>
                        <div className='col-md-12'>
                            <div className='table-responsive'>
                                <table className='table table-bordered'>
                                    <thead>
                                        <tr>
                                            <th>Fotografija</th>
                                            <th className='text-center'>Naziv</th>
                                            <th className='text-center'>Količina</th>
                                            <th className='text-center'>Cijena</th>
                                            <th>Ukloni</th>
                                        </tr>
                                    </thead>
                                    <tbody>
                                        { cart.map( (item) => {
                                            return (
                                                <tr>
                                                    <td width="10%">
                                                        <img src={ `http://localhost:8000/${product.photo}` } alt={ product.name } width="50px" height="50px" />
                                                    </td>
                                                    <td>{ item.product.name }</td>
                                                    <td width="15%" className='text-center'>{ item.product.price }</td>
                                                    <td width="15%">
                                                        <div className='input-group'>
                                                            <button type="button" className='input-group-text'>-</button>
                                                            <div className="form-control text-center">{ item.quantity }</div>
                                                            <button type="button" className='input-group-text'>+</button>
                                                        </div>
                                                    </td>
                                                    <td width="15%" className='text-center'>{ item.product.price * item.quantity }</td>
                                                    <td width="10%">
                                                        <button type="button" className='btn btn-danger btn-sm'>Ukloni iz košarice</button>
                                                    </td>
                                                </tr>
                                            )
                                        })}
                                    </tbody>
                                </table>
                            </div>
                        </div>                       
                    </div>
                </div>
            </div>
        </div>
    )
}

export default Cart;
