import React, { useEffect, useState } from 'react';
import { Link, useParams, useNavigate } from 'react-router-dom';

import axios from 'axios';
import swal from 'sweetalert';

function Product() {

    const { productKeyname } = useParams();

    const [category, setCategory] = useState([]);
    const [product, setProduct] = useState([]);

    const navigate = useNavigate();
    const [loading, setLoading] = useState(true);

    useEffect(() => {

        document.title = "Moja tržnica";

        axios.get(`/api/display-product/${productKeyname}`).then(res => {
            if (res.data.status === 200) {
                setCategory(res.data.category);
                setProduct(res.data.products);
                setLoading(false);
            } else if (res.data.status === 404) {
                swal("Error", res.data.message, "error");
                navigate('/categories');
            }
        });
    }, []);

    var displayData = '';
    if (loading) {
        return <h4>Dohvaćanje podataka u tijeku...</h4>
    } else {
            displayData = product.map( (item, idx) => {
                return (
                    <div className='col-md-3' key={ idx }>
                        <div className='card'>
                            <Link to={`/categories/${ item.category.keyname }/${ item.keyname }`}>
                                <img src={`http://localhost:8000/${ item.photo }`} className='w-100' alt={ item.name }/>
                            </Link>
                            <div className='card-body'>
                                <Link to={`/categories/${ item.category.keyname }/${ item.keyname }`}>
                                    <h5>{ item.name }</h5>
                                </Link>
                            </div>
                        </div>
                    </div>
                )
            });
    }

    return (
        <div>
            <div className='py-3 bg-warning'>
                <div className='container'>
                    <h6>{ category.name } / { product.name }</h6>
                </div>
            </div>

            <div className='py-3'>
                <div className='container'>
                    <div className='row'>
                        { displayData }
                    </div>
                </div>
            </div>
        </div>
    )
}

export default Product;
