import React from 'react';
import { BrowserRouter, Route, Routes, Navigate } from 'react-router-dom';

import AdminAccount from './components/admin/AdminAccount';
import Dashboard from './components/admin/Dashboard';
import Register from './components/frontend/auth/Register';
import Login from './components/frontend/auth/Login';
import Home from './components/frontend/Home';
import AppLayout from './layouts/admin/AppLayout';

import axios from 'axios';

axios.defaults.withCredentials = true;
axios.interceptors.request.use(function (config) {
  const token = localStorage.getItem('auth_token');
  config.headers.Authorization = token ? `Bearer ${token}` : '';
  return config;
});

axios.defaults.headers.post['Accept'] = 'application/json';
axios.defaults.headers.post['Content-Type'] = 'application/json';
axios.defaults.baseURL = "http://localhost:8000/";


function App() {
  return (
    <div className="App">
      <BrowserRouter>
        <Routes>
        <Route path="/" element={<AppLayout />} > 
          <Route path='/home' element={<Home />}
          />
          <Route 
            path='/register'
            element={ localStorage.getItem('auth_token') ? <Navigate to="/home" /> : <Register /> }
          />
          <Route 
            path='/login'
            element={ localStorage.getItem('auth_token') ? <Navigate to="/home" /> : <Login /> }
          />
        </Route>  




        <Route path="/admin" element={ localStorage.getItem('auth_token') ? <Navigate to="/home" /> : <Login />} > 
          <Route path='/admin/dashboard' element={<Dashboard />}/>
          <Route path='/admin/account' element={<AdminAccount />}/>
        </Route> 
        </Routes>
      </BrowserRouter>
    </div>
  );
}

export default App;
