import React, { useEffect, useState } from 'react';
import { Link, useParams, useNavigate } from 'react-router-dom';

import axios from 'axios';
import swal from 'sweetalert';

function Product() {

    const { productKeyname } = useParams();

    const [product, setProduct] = useState([]);

    const navigate = useNavigate();
    const [loading, setLoading] = useState(true);

    useEffect(() => {

        document.title = "Moja tržnica";

        axios.get(`/api/display-product/${productKeyname}`).then(res => {
            if (res.data.status === 200) {
                setProduct(res.data.product);
                setLoading(false);
            } else if (res.data.status === 404) {
                swal("Error", res.data.message, "error");
                navigate('/categories');
            }
        });
    }, []);

    var displayData = '';
    if (loading) {
        return <h4>Dohvaćanje podataka u tijeku...</h4>
    }

    return (
        <div>
            <div className='py-3 bg-warning'>
                <div className='container'>
                    <h6>{ product.name } / { product.name }</h6>
                </div>
            </div>

            <div className='py-3'>
                <div className='container'>
                    <div className='row'>
                        <div className="col-md-4 border-end">
                            <img src="" alt="" className="w-100" />
                        </div>
                        <div className='col-md-8'>
                            <h4>
                            { product.name }
                                <span className='float-end badge btn-sm btn-danger badge-pil'>Brand</span>
                            </h4>

                            <p>opis</p>

                            <div>
                                <label className='btn-sm btn-success px-4 mt-2'> Dostupno </label>

                                <div className='row'>
                                    <div className='col-md-3 mt-3'>
                                        <div className='input-group'>
                                            <button type="button" className="input-group-text">botun</button>
                                            <input type="text" className="form-control text-center" value="1" />
                                            <button type="button" className="input-group-text">+</button>
                                        </div>
                                    </div>
                                    <div className='col-md-3 mt-3'>
                                        <button type="button" className="btn btn-primary w-100">Dodaj ukošaricu</button>
                                    </div>
                                </div>
                            </div>

                            <button type="button" className="btn btn-danger mt-3">Dodaj na wishlistu</button>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default Product;
